mapper.select:
SELECT (SELECT COUNT(*) FROM IMGBOARD B WHERE B.DEL_YN IS NULL AND CATEGORY = #{category} AND B.NO <= A.NO) AS ROWNUM, A.* FROM ( SELECT NO, TITLE, CASE WHEN U.IS_DEL = 'Y' THEN '탈퇴한 사용자' ELSE U.USER_NIC END AS AUTHOR, RECOMMEND, VIEW_CNT, DATE_FORMAT(APPLY_DATE, '%Y-%m-%d') AS APPLY_FORMAT_DATE, DATE_FORMAT(B.UPDATE_DATE, '%Y-%m-%d') AS UPDATE_FORMAT_DATE, ( SELECT COUNT(*) FROM IMGCOMMENT C WHERE C.BOARD_NO = B.NO AND C.DEL_YN IS NULL ) AS COMMENT_CNT, IMG_PATH FROM IMGBOARD B JOIN USER U ON B.USER_ID = U.USER_ID WHERE DEL_YN IS NULL AND CATEGORY = #{category}
AND U.IS_DEL IS NULL AND UPPER(U.USER_NIC) LIKE CONCAT('%',UPPER(#{val}),'%')
AND TITLE LIKE CONCAT('%',#{val},'%')
AND (U.IS_DEL IS NULL AND UPPER(U.USER_NIC) LIKE CONCAT('%',UPPER(#{val}),'%') OR TITLE LIKE CONCAT('%',#{val},'%'))
ORDER BY APPLY_DATE DESC) A LIMIT #{limit} OFFSET #{offset}

SELECT * FROM ( SELECT CASE WHEN U.IS_DEL = 'Y' THEN '탈퇴한 사용자' ELSE U.USER_NIC END AS USER_NIC, COMMENT, DATE_FORMAT(APPLY_DATE, '%Y-%m-%d %H:%i:%s') AS APPLY_FORMAT_DATE, (SELECT COUNT(*) FROM IMGCOMMENT WHERE BOARD_NO = #{no} AND DEL_YN IS NULL )AS TOTAL FROM IMGCOMMENT JOIN USER U ON IMGCOMMENT.USER_ID = U.USER_ID WHERE BOARD_NO=#{no} AND DEL_YN IS NULL ORDER BY APPLY_FORMAT_DATE
DESC
ASC
) A LIMIT #{limit} OFFSET #{offset}
